import CardActionTypes from './cart.types';

const toggleCartHidden = () => ({
	type: CardActionTypes.TOGGLE_CART_HIDDEN,
});

const addItem = (item) => ({
	type: CardActionTypes.ADD_ITEM,
	payload: item,
});

export default toggleCartHidden;
